#include <iostream> #include <ctime> int main() { // تاریخ تولد میلادی را وارد کنید int birthYear, birthMonth, birthDay; std::cout << "سال تولد: "; std::cin >> birthYear; std::cout << "ماه تولد (1 تا 12): "; std::cin >> birthMonth; std::cout << "روز تولد: "; std::cin >> birthDay; // دریافت زمان فعلی std::time_t now = std::time(nullptr); std::tm* localTime = std::localtime(&now); // تاریخ تولد را تنظیم کنید std::tm birthday = {}; birthday.tm_year = birthYear - 1900; birthday.tm_mon = birthMonth - 1; birthday.tm_mday = birthDay; // محاسبه مدت زمان گذشته از تولد تا الان std::time_t diff = std::difftime(now, std::mktime(&birthday)); // تبدیل زمان به روز، ساعت، دقیقه و ثانیه int days = diff / (60 * 60 * 24); int hours = (diff % (60 * 60 * 24)) / (60 * 60); int minutes = (diff % (60 * 60)) / 60; int seconds = diff % 60; std::cout << "از تولد شما تا الان گذشته است:\n"; std::cout << days << " روز، " << hours << " ساعت، " << minutes << " دقیقه، " << seconds << " ثانیه\n"; return 0; }
