#include <iostream> using namespace std; int main() { char a[100], b[100]; int bjaded[100]; int c[100]; int temp; int i, j, a1, b1, c1, t; int a2[100], b2[100]; cout << "عدد طولانی اول را وارد کنید: "; cin >> a; cout << "عدد طولانی دوم را وارد کنید: "; cin >> b; a1 = 0; for (i = 0; i < 100; i++) { a2[i] = 0; if (a[i] >= '0' && a[i] <= '9') { a2[i] = a[i] - 48; a1++; } } b1 = 0; for (i = 0; i < 100; i++) { b2[i] = 0; if (b[i] >= '0' && b[i] <= '9') { b2[i] = b[i] - 48; b1++; } } t = 0; if (b1 > a1) { t = 1; for (i = 0; i <= b1; i++) { temp = a2[i]; a2[i] = b2[i]; b2[i] = temp; } temp = a1; a1 = b1; b1 = temp; } c1 = a1 - b1; for (i = c1; i <= a1; i++) { bjaded[i] = b2[i - c1]; } temp = 0; for (i = a1; i >= 0; i--) { c[i] = a2[i] + bjaded[i] + temp; temp = 0; if (c[i] > 9) { temp = 1; c[i] = c[i] - 10; } } cout << "حاصل جمع دو عدد طولانی: "; if (temp > 0) cout << temp; for (i = 0; i <= a1; i++) cout << c[i]; return 0; }
